        setTimeout(function(){
            $description = $(".description");
    
            $('.enabled').hover(function() {
                if($(this).attr('title') != undefined){
                    $description.addClass('active');
                    $description.html($(this).attr('title'));
                }else{
                    $description.removeClass('active');
                }
            }, function() {
                $description.removeClass('active');
                $description.html('');
            });
        
            $(document).on('mousemove', function(e){
              $description.css({
                left:  e.pageX-100,
                top:   e.pageY-550,
              });
            });
        }, 1000);
        
    </script>